import React from 'react';
import './'

function NoteList({ notes, onSelect }) {
  return (
    <div id="notesList" style={{overflowY: "scroll"}}>
      {notes.map((note) => (
        <div id="note" key={note.id} onClick={() => onSelect(note)}>
          <div style={{display: 'flex' , flexDirection: 'column'}}>           
             <h4 id="noteTile">{note.title}</h4>
             <h6 id="noteContent" style={{fontSize: '12px', marginBottom: '10px', marginTop: '5px'}}>{note.description}</h6>
             <p id="taskStatus" style={{fontSize: '14px'}}>
              {note.completed ? 'Task Completed' : 'Task In Progress'}
            </p>
          </div>
        </div>
      ))}
    </div>
  );
}

export default NoteList;
