import React from 'react';
import './TaskWid'

const TaskWidgets = ({ completedTodos, pendingTodos }) => {
  return (
    <div className="task-widgets" style={{fontSize: '12px', display:'flex', flexDirection: 'row', gap: '5vw'}}>
      <div className="widget">
        <h3>Completed Tasks</h3>
        <p>{completedTodos}</p>
      </div>
      <div className="widget">
        <h3>Pending Tasks</h3>
        <p>{pendingTodos}</p>
      </div>
    </div>
  );
};

export default TaskWidgets;
