import './Main.css';
import React, { useState } from 'react';

function Main() {
  const [showButtons, setShowButtons] = useState(false);
  const [notes, setNotes] = useState([]); // State to store all notes
  const [noteTitle, setNoteTitle] = useState('');
  const [noteContent, setNoteContent] = useState('');

  const handleNewNoteBtn = () => {
    setShowButtons(!showButtons);
  };

  const handleAddNote = () => {
    if (noteTitle && noteContent) {
      setNotes([...notes, { title: noteTitle, content: noteContent }]); // Add new note
      setNoteTitle(''); // Clear input fields
      setNoteContent('');
    }
  };

  const handleDeleteNote = (index) => {
    const updatedNotes = notes.filter((_, i) => i !== index); // Remove the note at the specified index
    setNotes(updatedNotes);
  };

  return (
    <div id='mainBody'>
      <div id="notesSideBar">
        <div id="newNote">
          <button id="newNoteBtn" onClick={handleNewNoteBtn}>
            <span>+</span>
          </button>
        </div>

        {showButtons && (
          <div>
            <div id="noteInfo">
              <input 
                type="text" 
                id="title" 
                placeholder='Enter title' 
                value={noteTitle}
                onChange={(e) => setNoteTitle(e.target.value)}
              />
              <textarea 
                id="text" 
                placeholder='Enter text' 
                value={noteContent}
                onChange={(e) => setNoteContent(e.target.value)}
              />
            </div>

            <div id="buttonClass">
              <button onClick={handleAddNote}>Add Note</button>
              <button onClick={() => setShowButtons(false)}>Cancel</button>
            </div>
          </div>
        )}
      </div>

      <div id="notesDashboard">
        <h1 id="notedHeader">NOTES</h1>
        <div id="notesList">
          {notes.map((note, index) => (
            <div id='note' key={index}>
              <h5 id="noteTitle">{note.title}</h5>
              <h6 id="noteContent">{note.content}</h6>
              <button onClick={() => handleDeleteNote(index)}>Delete</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Main;
