import './Main.css';
import React, { useState, useEffect } from 'react';
import NoteForm from '../notes/NoteForm';
import NoteList from '../notes/NotesList';
import NewNoteButton from '../notes/NewNoteButton';
import NoteButtons from '../notes/NoteButtons';

function Main() {
  const [showButtons, setShowButtons] = useState(false);
  const [updateButtons, setUpdateButtons] = useState(false);
  const [showFields, setShowFields] = useState(false); //for showing the to do insert UI
  const [todos, setTodos] = useState([]); 
  const [todoTitle, settodoTitle] = useState('');
  const [todoContent, settodoContent] = useState('');
  const [selectedNote, setSelectedNote] = useState(null);

  //initial fetch
  useEffect(() => {
    const fetchTodos = async () => {
      try {
        const response = await fetch('http://localhost:5000/todos'); 
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setTodos(data);
      } catch (error) {
        console.error('There was an error fetching the todos ', error);
      }
    };

    fetchTodos();
  }, []);

  //adding the UI for the adding todo field
  const handleNewNoteBtn = () => {
    settodoContent('');
    settodoTitle('');
    setShowButtons(true);
    setUpdateButtons(false); 
    setShowFields(true); 
  };

  const handleDeleteBtnNew = () => {
    setShowButtons(false);
    setShowFields(false);
  };

  //adding the to do into the database
  const handleAddBtn = async () => {
    const newTodo = {
      title: todoTitle,
      content: todoContent,
    };

    try {
      const response = await fetch('http://localhost:5000/todos', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newTodo),
      });
      
      const addedTodo = await response.json();
      setTodos([...todos, addedTodo]);
      setShowButtons(false);
      setShowFields(false);
      settodoTitle('');
      settodoContent('');
    } catch (error) {
      console.error('Error adding todo: ', error);
    }
  };

  //when selecting the to do card we get the to do UI field to show up
  const openNote = (todo) => {
    setShowButtons(false);
    setShowFields(true);
    setSelectedNote(todo);
    settodoTitle(todo.title);
    settodoContent(todo.content);
    setUpdateButtons(true);
  };

  //delete a todo
  const handleDeleteBtnUpdate = async () => {
    try{
      const response = await fetch(`http://localhost:5000/todos/${selectedNote.id}`, {
        method: 'DELETE',
      });

      const updatedTodos = todos.filter(todo => todo !== selectedNote);
      setTodos(updatedTodos);
      setUpdateButtons(false);
      setShowFields(false);
      setSelectedNote(null);
    }catch(error){
      console.error('Error deleting todo:', error);

    }

  };

  const handleUpdateButton = () => {
    const updatedTodos = todos.map(todo => 
      todo === selectedNote 
        ? { ...todo, title: todoTitle, content: todoContent } 
        : todo
    );

    setTodos(updatedTodos);
    settodoTitle('');
    settodoContent('');
    setSelectedNote(null);
    setUpdateButtons(false);
    setShowFields(false);
  };

  const handleTitle = (e) => {
    settodoTitle(e.target.value);
  };

  const handleContent = (e) => {
    settodoContent(e.target.value);
  };

  return (
    <div id='mainBody'>
      <div id="notesSideBar">
        <NewNoteButton onClick={handleNewNoteBtn} />
        {showFields && (
          <NoteForm
            title={todoTitle}
            content={todoContent}
            onTitleChange={handleTitle}
            onContentChange={handleContent}
          />
        )}
        <NoteButtons
          onAdd={handleAddBtn}
          onDeleteNew={handleDeleteBtnNew}
          onUpdate={handleUpdateButton}
          onDeleteUpdate={handleDeleteBtnUpdate}
          showAdd={showButtons}
          showUpdate={updateButtons}
        />
      </div>
      <div id="notesDashboard">
        <h1 id="notedHeader">TO DO</h1>
        <NoteList notes={todos} onSelect={openNote} />
      </div>
    </div>
  );
}

export default Main;
