import './Main.css';
import React, { useState } from 'react';

function Main() {

  const [showButtons, setShowButtons] = useState(false);
  const handleNewNoteBtn = () =>{
    setShowButtons(!showButtons);
  }

  return (
    <div id='mainBody'>
        <div id="notesSideBar">
         
          <div id="newNote">
            <button id="newNoteBtn" onClick={handleNewNoteBtn}>
              <span>+</span>
            </button>
          </div>

          {showButtons && (
            <div>
          <div id="noteInfo">
              <input type="text" id="title" placeholder='Enter title'/>
              <textarea type="text" id="text" placeholder='Enter text'/>
          </div>

          <div id="buttonClass">
            <button>Add Note</button>
            <button>Delete</button>
          </div>
          </div>
          )}

        </div>
        <div id="notesList">
          <h1 id="notedHeader"> NOTES </h1>
          <div id='note'>
            
          </div>
        </div>
    </div>
  );
}

export default Main;
