import './Main.css';
import React, { useState, useEffect } from 'react';
import NoteForm from '../notes/NoteForm';
import NoteList from '../notes/NotesList';
import NewNoteButton from '../notes/NewNoteButton';
import NoteButtons from '../notes/NoteButtons';

function Main() {
  const [showButtons, setShowButtons] = useState(false);
  const [updateButtons, setUpdateButtons] = useState(false);
  const [showFields, setShowFields] = useState(false);
  const [notes, setNotes] = useState([]);
  const [noteTitle, setNoteTitle] = useState('');
  const [noteContent, setNoteContent] = useState('');
  const [selectedNote, setSelectedNote] = useState();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchNotes = async () => {
      try {
        const response = await fetch('http://localhost:8080/todos');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        console.log('Fetched data:', data);
        setNotes(data);  // Update state with fetched notes
      } catch (err) {
        console.error('Fetch error:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchNotes();
  }, []);

  const handleNewNoteBtn = () => {
    setNoteContent('');
    setNoteTitle('');
    setShowButtons(true);
    setUpdateButtons(false);
    setShowFields(true);
  };

  const handleDeleteBtnNew = () => {
    setShowButtons(false);
    setShowFields(false);
  };

  const handleAddBtn = () => {
    const newNote = {
      title: noteTitle,
      content: noteContent,
    };

    setNotes([...notes, newNote]);
    setShowButtons(false); // Close the button panel after adding
    setShowFields(false); // Close the input fields after adding
  };

  const openNote = (note) => {
    setShowButtons(false);
    setShowFields(true);
    setSelectedNote(note);

    setNoteTitle(note.title);
    setNoteContent(note.content); // Update to use `content` not `description`
    setUpdateButtons(true); // Show update button
  };

  const handleDeleteBtnUpdate = () => {
    const removedNote = notes.filter(note => note !== selectedNote);
    setNotes(removedNote);
    
    setUpdateButtons(false);
    setShowFields(false);
  };

  const handleUpdateButton = () => {
    const updatedNotes = notes.map(note => 
      note === selectedNote 
        ? { ...note, title: noteTitle, content: noteContent } 
        : note
    );

    setNotes(updatedNotes);
    setNoteTitle('');
    setNoteContent('');
    setSelectedNote(null);
    setUpdateButtons(false);
    setShowFields(false);
  };

  const handleTitle = (e) => {
    setNoteTitle(e.target.value);
  };

  const handleContent = (e) => {
    setNoteContent(e.target.value);
  };

  return (
    <div id='mainBody'>
      <div id="notesSideBar">
        <NewNoteButton onClick={handleNewNoteBtn} />

        {showFields && (
          <NoteForm
            title={noteTitle}
            content={noteContent}
            onTitleChange={handleTitle}
            onContentChange={handleContent}
          />
        )}
        
        <NoteButtons
          onAdd={handleAddBtn}
          onDeleteNew={handleDeleteBtnNew}
          onUpdate={handleUpdateButton}
          onDeleteUpdate={handleDeleteBtnUpdate}
          showAdd={showButtons}
          showUpdate={updateButtons}
        />
      </div>
      
      <div id="notesDashboard">
        <h1 id="notedHeader"> TO DO </h1>
        <NoteList notes={notes} onSelect={openNote} />
      </div>
    </div>
  );
}

export default Main;
