import './Main.css';
import React, { useState, useEffect } from 'react';
import NoteForm from '../notes/NoteForm';
import NoteList from '../notes/NotesList';
import NewNoteButton from '../notes/NewNoteButton';
import NoteButtons from '../notes/NoteButtons';

function Main() {

  const [showButtons, setShowButtons] = useState(false);
  const [updateButtons, setUpdateButtons] = useState(false);
  const [showFields, setShowFields] = useState(false);
  const [todos, setTodos] = useState([]);
  const [todoTitle, settodoTitle] = useState('');
  const [todoContent, settodoContent] = useState('');
  const [selectedNote, setSelectedNote ] = useState();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTodods = async () => {
      try {
        const response = await fetch('http://localhost:5000/todos'); 
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setTodos(data);
      } catch (err) {
        setError(err.message);
    };
  }

    fetchTodods();
  }, []);

  const handleNewNoteBtn = () =>{
    settodoContent('');
    settodoTitle('');
    setShowButtons(true);
    setUpdateButtons(false); 
    setShowFields(true); 
  };

  const handleDeleteBtnNew = () => {
    setShowButtons(false);
    setShowFields(false);
  };

  const handleAddBtn = async() =>{
      const newNote = {
        title: todoTitle,
        content: todoContent,
      };

      try{
        const response = await fetch('http://localhost:5000/todos', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newNote),
     });
    
      const addedTodo = await response.json();
      setTodos([...todos, addedNote]);
      setShowButtons(false);
      setShowFields(false);
      settodoTitle('');
      settodoContent('');
    } catch (error) {
      console.error('Error adding todo:', error);
    }
  };

  const openNote = (note) => {
    setShowButtons(false);
    setShowFields(true);
    setSelectedNote(note);

    const title = note.title;
    const content = note.content;

    settodoTitle(title);
    settodoContent(content);
    setUpdateButtons(!updateButtons);

  };

  const handleDeleteBtnUpdate = () => {
    
    const removedNote = notes.filter( note => note !== selectedNote);
    setNotes(removedNote);
    
    setUpdateButtons(!updateButtons);
    setShowFields(!showFields);
  };

  const handleUpdateButton = () => {
    const updatedNotes = notes.map(note => 
      note === selectedNote 
          ? { ...note, title: todoTitle, content: todoContent } 
          : note
  );

  setNotes(updatedNotes);

  settodoTitle('');
  settodoContent('');
  setSelectedNote(null);
  setUpdateButtons(false);
  setShowFields(false);
  }

  const handleTitle = (e) => {
    settodoTitle(e.target.value);
  };

  const handleContent = (e) => {
    settodoContent(e.target.value);
  };

  return (
    <div id='mainBody'>
      <div id="notesSideBar">
         
      <NewNoteButton onClick={handleNewNoteBtn} />

        {showFields && (
          <NoteForm
            title={todoTitle}
            content={todoContent}
            onTitleChange={handleTitle}
            onContentChange={handleContent}
          />
         )}
        <NoteButtons
          onAdd={handleAddBtn}
          onDeleteNew={handleDeleteBtnNew}
          onUpdate={handleUpdateButton}
          onDeleteUpdate={handleDeleteBtnUpdate}
          showAdd={showButtons}
          showUpdate={updateButtons}
        />

      </div>
      <div id="notesDashboard">
        <h1 id="notedHeader"> TO DO </h1>
        <NoteList notes={notes} onSelect={openNote} />
      </div>
    </div>
  );
}

export default Main;
