import './Main.css';
import React, { useState } from 'react';

function Main() {

  const [showButtons, setShowButtons] = useState(false);
  const [notes, setNotes] = useState([]);
  const [noteTitle, setNoteTitle] = useState('');
  const [noteContent, setNoteContent] = useState('');


  const handleNewNoteBtn = () =>{
    setShowButtons(!showButtons);
  };

  const handleDeleteBtn = () => {
    setShowButtons(!showButtons);
  };

  const handleAddBtn = () =>{
      const newNote = {
        title: noteTitle,
        content: noteContent,
      };

      setNotes([...notes, newNote]);
      setNoteContent('');
      setNoteTitle('');
      setShowButtons(!showButtons);
  };

  const handleTitle = (e) => {
    setNoteTitle(e.target.value);
    console.log(e.target.value);
  };

  const handleContent = (e) => {
    setNoteContent(e.target.value);
    console.log(e.target.value);
  };

  const openNote = () => {

  };

  return (
    <div id='mainBody'>
        <div id="notesSideBar">
         
          <div id="newNote">
            <button id="newNoteBtn" onClick={handleNewNoteBtn}>
              <span>+</span>
            </button>
          </div>

          {showButtons && show && (
            <div>
          <div id="noteInfo">
              <input type="text" id="title" placeholder='Enter title' onChange={handleTitle}/>
              <textarea type="text" id="text" placeholder='Enter text' onChange={handleContent}/>
          </div>

          <div id="buttonClass">
            <button onClick={handleAddBtn}>Add Note</button>
            <button onClick={handleDeleteBtn}>Delete</button>
          </div>
          </div>
          )}

        </div>
        <div id="notesDashboard">
          <h1 id="notedHeader"> NOTES </h1>
          <div id="notesList">
          {notes.map((note, index) => (
            <div id='note' key={index} onClick={openNote}>
              <h5 id="noteTile">{note.title}</h5>
              <h6 id="noteContent">{note.content}</h6>
            </div>
          ))}
        </div>

        </div>
    </div>
  );
}

export default Main;
