import './Main.css';
import React, { useState } from 'react';

function Main() {

  const [showButtons, setShowButtons] = useState(false);
  const [updateButtons, setUpdateButtons] = useState(false);
  const [showFields, setShowFields] = useState(false);
  const [notes, setNotes] = useState([]);
  const [noteTitle, setNoteTitle] = useState('');
  const [noteContent, setNoteContent] = useState('');
  const [selectedNote, setSelectedNote ] = useState();


  const handleNewNoteBtn = () =>{
    setNoteContent('');
    setNoteTitle('');
    setShowButtons(true);
    setUpdateButtons(false); 
    setShowFields(true); 
  };

  const handleDeleteBtnNew = () => {
    setShowButtons(false);
    setShowFields(false);
  };

  const handleDeleteBtnUpdate = () => {
    
    const removedNote = notes.filter( note => note !== selectedNote);
    setNotes(removedNote);
    
    setUpdateButtons(!updateButtons);
    setShowFields(!showFields);
  };

  const handleAddBtn = () =>{
      const newNote = {
        title: noteTitle,
        content: noteContent,
      };

      setNotes([...notes, newNote]);
      setShowButtons(!showButtons);
      setShowFields(!showFields);
  };

  const openNote = (note) => {
    setShowButtons(false);
    setShowFields(true);
    setSelectedNote(note);

    const title = note.title;
    const content = note.content;

    setNoteTitle(title);
    setNoteContent(content);
    setUpdateButtons(!updateButtons);

  };

  return (
    <div id='mainBody'>
        <div id="notesSideBar">
         
          <div id="newNote">
            <button id="newNoteBtn" onClick={handleNewNoteBtn}>
              <span>+</span>
            </button>
          </div>

          {showFields && (
          <div id="noteInfo">
              <input type="text" id="title" placeholder='Enter title' onChange={handleTitle} value={noteTitle}/>
              <textarea type="text" id="text" placeholder='Enter text' onChange={handleContent} value={noteContent}/>
          </div>
          )}

          {showButtons && (
          <div id="buttonClass">
            <button onClick={handleAddBtn}>Add Note</button>
            <button onClick={handleDeleteBtnNew}>Delete</button>
          </div>
          )}

        {updateButtons && (
          <div id="buttonClass">
            <button onClick={handleAddBtn}>Update</button>
            <button onClick={() => handleDeleteBtnUpdate()}>Delete</button>
          </div>
          )}

        </div>
        <div id="notesDashboard">
          <h1 id="notedHeader"> NOTES </h1>
          <div id="notesList">
          {notes.map((note, index) => (
            <div id='note' key={index} onClick={() => openNote(note)}>
              <h5 id="noteTile">{note.title}</h5>
              <h6 id="noteContent">{note.content}</h6>
            </div>
          ))}
        </div>

        </div>
    </div>
  );
}

export default Main;
