import './Main.css';
import React, { useState, useEffect, useCallback, useMemo } from 'react';
import NoteForm from '../notes/NoteForm';
import NoteList from '../notes/NotesList';
import NewNoteButton from '../notes/NewNoteButton';
import NoteButtons from '../notes/NoteButtons';
import TaskWidgets from '../notes/TaskWidgets';

function Main() {
  const [showButtons, setShowButtons] = useState(false);
  const [updateButtons, setUpdateButtons] = useState(false);
  const [showFields, setShowFields] = useState(false); 
  const [todos, setTodos] = useState([]); 
  const [todoTitle, settodoTitle] = useState('');
  const [todoContent, settodoContent] = useState('');
  const [selectedNote, setSelectedNote] = useState(null);
  const [isCompleted, setIsCompleted] = useState(false);
  const [filterText, setFilterText] = useState(''); 

  //initial fetch
  useEffect(() => {
    const fetchTodos = async () => {
      try {
        const response = await fetch('http://localhost:8080/todos'); 
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setTodos(data);
      } catch (error) {
        console.error('There was an error fetching the todos ', error);
      }
    };

    fetchTodos();
  }, []);

  //filtered notes based on the filter input
  const filteredTodos = useMemo(() => {
    return todos.filter(todo => 
      todo.title.toLowerCase().includes(filterText.toLowerCase()) ||
      todo.description.toLowerCase().includes(filterText.toLowerCase())
    );
  }, [todos, filterText]);

  //calculating the completion of the tasks and 
  const completedTodos = useMemo(() => {
    return todos.filter(todo => todo.completed).length;
  }, [todos]);

  const pendingTodos = useMemo(() => {
    return todos.filter(todo => !todo.completed).length;
  }, [todos]);

  //prepare the UI for entering a new note
  const handleNewNoteBtn = () => {
    settodoContent('');
    settodoTitle('');
    setShowButtons(true);
    setUpdateButtons(false); 
    setShowFields(true); 
  };

  //closing the input UI
  const handleDeleteBtnNew = () => {
    setShowButtons(false);
    setShowFields(false);
  };

  //handle adding a new note
  const handleAddBtn = useCallback(async () => {
    if(todoTitle==="" || todoContent === ""){
      alert("please fill all fields");
      return;
    }

    const newTodo = {
      title: todoTitle,
      description: todoContent,
      completed: isCompleted,
    };

    try {
      const response = await fetch('http://localhost:8080/todos', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newTodo),
      });
      
      const addedTodo = await response.json();
      setTodos([...todos, addedTodo]);
      setShowButtons(false);
      setShowFields(false);
      setIsCompleted(false);
      settodoTitle('');
      settodoContent('');
    } catch (error) {
      console.error('Error adding todo: ', error);
    }
  }, [todoTitle, todoContent, todos, isCompleted]);

  //opening existing note/to do for editing 
  const openNote = (todo) => {
    setShowButtons(false);
    setShowFields(true);
    setSelectedNote(todo);
    settodoTitle(todo.title);
    settodoContent(todo.description);
    setIsCompleted(todo.completed);
    setUpdateButtons(true);
  };

  //handling deletion of the selected note/to do
  const handleDeleteBtnUpdate = useCallback(async () => {
    try {
      const response = await fetch(`http://localhost:8080/todos/${selectedNote.id}`, {
        method: 'DELETE',
      });

      const updatedTodos = todos.filter(todo => todo !== selectedNote);
      setTodos(updatedTodos);
      setUpdateButtons(false);
      setShowFields(false);
      setSelectedNote(null);
    } catch (error) {
      console.error('Error deleting todo:', error);
    }
  }, [selectedNote, todos]);

  const handleUpdateButton = useCallback(async () => {
    if(todoTitle==="" || todoContent === ""){
      alert("please fill all fields");
      return;
    }

    const updatedTodo = {
      ...selectedNote,
      title: todoTitle,
      description: todoContent,
      completed: isCompleted,
    };
  
    try {
      const response = await fetch(`http://localhost:8080/todos/${selectedNote.id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedTodo),
      });
      const updatedTodoFromServer = await response.json();
  
      const updatedTodos = todos.map(todo =>
        todo.id === updatedTodoFromServer.id ? updatedTodoFromServer : todo
      );
  
      setTodos(updatedTodos);
      settodoTitle('');
      settodoContent('');
      setIsCompleted(false);
      setSelectedNote(null);
      setUpdateButtons(false);
      setShowFields(false);
  
    } catch (error) {
      console.error('Error updating todo:', error);
    }
  }, [selectedNote, todoTitle, todoContent, isCompleted, todos]);

  const handleTitle = (e) => {
    settodoTitle(e.target.value);
  };

  const handleContent = (e) => {
    settodoContent(e.target.value);
  };

  const handleCompletion = (e) => {
    setIsCompleted(e.target.checked);
  };

  //handle the filter input change
  const handleFilterChange = (e) => {
    setFilterText(e.target.value);
  };

  return (
    <div id='mainBody'>
      <div id="notesSideBar">
        <div id="notesSideBarTop" style={{display: 'flex', flexDirection: 'row', gap: "5vw"}}>
          <NewNoteButton onClick={handleNewNoteBtn} />
          <TaskWidgets completedTodos={completedTodos} pendingTodos={pendingTodos}/>
        </div>
        {showFields && (
          <NoteForm
            title={todoTitle}
            content={todoContent}
            completed={isCompleted}
            onTitleChange={handleTitle}
            onContentChange={handleContent}
            onChangeCompletion={handleCompletion}
          />
        )}
        <NoteButtons
          onAdd={handleAddBtn}
          onDeleteNew={handleDeleteBtnNew}
          onUpdate={handleUpdateButton}
          onDeleteUpdate={handleDeleteBtnUpdate}
          showAdd={showButtons}
          showUpdate={updateButtons}
        />
      </div>
      <div id="notesDashboard">
        <div id="notesDashboardHeader">
          <h1 id="notedHeader">TO DO</h1>
          <input 
            type="text" 
            placeholder="Filter tasks" 
            value={filterText} 
            onChange={handleFilterChange} 
          />
        </div>
        <NoteList notes={filteredTodos} onSelect={openNote} />
      </div>
    </div>
  );
}

export default Main;
